import styled from '@emotion/styled'
import { StyledCheckIconProps, StyledLabelProps } from './Check.types'

export const CheckWrapper = styled.div`
  display: flex;
  width: 100%;
  align-items: center;
`

export const Label = styled.label<StyledLabelProps>`
  display: flex;
  align-items: center;
  flex-grow: 1;
  cursor: pointer;
  font-weight: ${({ $isEmphasis }) => $isEmphasis && 700};
`

export const Input = styled.input`
  display: none;
`

const CHECKED_ICON_URL =
  'data:image/svg+xml;base64,'
const UNCHECKED_ICON_URL =
  'data:image/svg+xml;base64,'

export const CheckIcon = styled.i<StyledCheckIconProps>`
  display: inline-block;
  background-size: cover;
  background-repeat: no-repeat;
  outline: none;
  vertical-align: bottom;
  min-width: 20px;
  width: 20px;
  height: 20px;

  background-image: ${({ $isChecked }) =>
    $isChecked ? `url(${CHECKED_ICON_URL})` : `url(${UNCHECKED_ICON_URL})`};

  margin-right: 8px;
`

const RIGHT_ARROW_URL =
  'data:image/svg+xml;base64,'

export const RightArrowButton = styled.button`
  width: 20px;
  height: 20px;
  display: inline-block;
  background-size: cover;
  background-repeat: no-repeat;
  outline: none;
  background-image: url(${RIGHT_ARROW_URL});
  cursor: pointer;
  border: 0;
  background-color: #fff;
  font-size: 0;
  flex-shrink: 0;
`
